import { Component } from '@angular/core';

@Component({
  selector: 'app-root',
  templateUrl: './app.component.html',
  styleUrls: ['./app.component.css']
})
export class AppComponent {
  title = 'angular-lifecycle-hook';
  inputVal: string = '';
  toDestroy: boolean = false;
  constructor(){
    console.log('App Component constructor called!');
  }

  // ngAfterViewInit(){
  //   console.log('ngAfterViewInit Hook of AppComponent called');
  // }
  // ngAfterViewChecked(){
  //   console.log('ngAfterViewChecked Hook of AppComponent called');
  // }

  onBtnClicked(inputEl: HTMLInputElement){
      this.inputVal = inputEl.value;
  }

  DestroyComponent(){
    this.toDestroy = !this.toDestroy;
  }
}
<div class="container">
    <input type="text" #inputEl (focus)="0">
    <button (click)="onBtnClicked(inputEl)">Submit</button>
    <app-demo [message]="inputVal" *ngIf="!toDestroy">
        <p #temp>User has eneterd folloeing text: {{ inputVal }}</p>
    </app-demo>
    <br><br>
    <button (click)="DestroyComponent()">Show / Hide</button>
</div>
